--

CREATE FUNCTION public.admin_set_user_organisation(target_user_id uuid, new_organisation_id uuid) RETURNS json
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
DECLARE
    result json;
    old_organisation_id uuid;
BEGIN
    -- Check if the current user is an admin
    IF NOT is_admin() THEN
        RETURN json_build_object('success', false, 'message', 'Access denied. Admin privileges required.');
    END IF;
    
    -- Check if target user exists
    IF NOT EXISTS (SELECT 1 FROM auth.users WHERE id = target_user_id) THEN
        RETURN json_build_object('success', false, 'message', 'Target user not found.');
    END IF;
    
    -- Check if organisation exists (if not null)
    IF new_organisation_id IS NOT NULL AND NOT EXISTS (SELECT 1 FROM organisations WHERE id = new_organisation_id) THEN
        RETURN json_build_object('success', false, 'message', 'Organisation not found.');
    END IF;
    
    -- Get current organisation_id for response
    SELECT organisation_id INTO old_organisation_id 
    FROM profiles 
    WHERE id = target_user_id;
    
    -- Update the user's profile
    UPDATE profiles 
    SET organisation_id = new_organisation_id,
        updated_at = now()
    WHERE id = target_user_id;
    
    -- Check if update was successful
    IF FOUND THEN
        SELECT json_build_object(
            'success', true, 
            'message', 'User organisation updated successfully',
            'user_id', target_user_id,
            'old_organisation_id', old_organisation_id,
            'new_organisation_id', new_organisation_id
        ) INTO result;
        
        RETURN result;
    ELSE
        RETURN json_build_object('success', false, 'message', 'Failed to update user organisation.');
    END IF;
END;
$$;


