--
-- Name: admin_set_user_role(uuid, public.user_role_enum, uuid); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public.admin_set_user_role(target_user_id uuid, new_role public.user_role_enum, assigned_by_user_id uuid DEFAULT NULL::uuid) RETURNS json
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
DECLARE
    result json;
    old_role public.user_role_enum;
    role_record_id uuid;
    assigning_user_id uuid;
BEGIN
    -- Check if the current user is an admin
    IF NOT public.is_admin() THEN
        RETURN json_build_object('success', false, 'message', 'Access denied. Admin privileges required.');
    END IF;
    
    -- Check if target user exists
    IF NOT EXISTS (SELECT 1 FROM auth.users WHERE id = target_user_id) THEN
        RETURN json_build_object('success', false, 'message', 'Target user not found.');
    END IF;
    
    -- Set assigning_user_id to current user if not provided
    IF assigned_by_user_id IS NULL THEN
        assigning_user_id := auth.uid();
    ELSE
        assigning_user_id := assigned_by_user_id;
    END IF;
    
    -- Check if assigning user exists and is an admin
    IF NOT EXISTS (SELECT 1 FROM public.roles WHERE user_id = assigning_user_id AND role = 'admin') THEN
        RETURN json_build_object('success', false, 'message', 'Assigned by user must be an admin.');
    END IF;
    
    -- Check if user already has a role record
    SELECT id, role INTO role_record_id, old_role
    FROM public.roles 
    WHERE user_id = target_user_id;
    
    IF FOUND THEN
        -- Update existing role
        UPDATE public.roles 
        SET role = new_role,
            assigned_by = assigning_user_id,
            assigned_at = now()
        WHERE id = role_record_id;
        
        SELECT json_build_object(
            'success', true, 
            'message', 'User role updated successfully',
            'user_id', target_user_id,
            'old_role', old_role,
            'new_role', new_role,
            'assigned_by', assigning_user_id
        ) INTO result;
        
        RETURN result;
    ELSE
        -- Create new role record
        INSERT INTO public.roles (user_id, role, assigned_by)
        VALUES (target_user_id, new_role, assigning_user_id)
        RETURNING id INTO role_record_id;
        
        SELECT json_build_object(
            'success', true, 
            'message', 'User role assigned successfully',
            'user_id', target_user_id,
            'new_role', new_role,
            'assigned_by', assigning_user_id
        ) INTO result;
        
        RETURN result;
    END IF;
END;
$$;


