-- Name: moderate_blog_comment(uuid, public.comment_moderation_status_enum, text); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public.moderate_blog_comment(comment_id_param uuid, new_status public.comment_moderation_status_enum, moderator_notes text DEFAULT NULL::text) RETURNS boolean
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
DECLARE
    comment_org_id uuid;
    user_org_id uuid;
BEGIN
    -- Get the organisation ID of the blog post this comment belongs to
    SELECT bp.organisation_id INTO comment_org_id
    FROM public.blog_post_comments bpc
    JOIN public.blog_posts bp ON bp.id = bpc.blog_post_id
    WHERE bpc.id = comment_id_param;
    
    -- Get the user's organisation ID
    SELECT organisation_id INTO user_org_id
    FROM public.profiles
    WHERE id = auth.uid();
    
    -- Check if user is admin or belongs to the same organisation
    IF NOT (
        EXISTS (SELECT 1 FROM public.roles WHERE user_id = auth.uid() AND role = 'admin') OR
        (comment_org_id = user_org_id AND user_org_id IS NOT NULL)
    ) THEN
        RETURN false;
    END IF;
    
    -- Update the comment moderation status
    UPDATE public.blog_post_comments
    SET 
        moderation_status = new_status,
        moderation_notes = moderator_notes,
        moderated_by = auth.uid(),
        moderated_at = now()
    WHERE id = comment_id_param;
    
    RETURN true;
END;
$$;


