-- Name: update_blog_post_stats(); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public.update_blog_post_stats() RETURNS trigger
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
BEGIN
    IF TG_OP = 'INSERT' THEN
        IF TG_TABLE_NAME = 'blog_post_likes' THEN
            UPDATE public.blog_posts 
            SET like_count = like_count + 1 
            WHERE id = NEW.blog_post_id;
        ELSIF TG_TABLE_NAME = 'blog_post_comments' THEN
            -- Count all comments except hidden ones
            IF NEW.moderation_status != 'hidden' THEN
                UPDATE public.blog_posts 
                SET comment_count = comment_count + 1 
                WHERE id = NEW.blog_post_id;
            END IF;
        END IF;
        RETURN NEW;
    ELSIF TG_OP = 'UPDATE' THEN
        IF TG_TABLE_NAME = 'blog_post_comments' THEN
            -- Handle comment visibility changes
            IF OLD.moderation_status != NEW.moderation_status THEN
                IF NEW.moderation_status = 'hidden' AND OLD.moderation_status != 'hidden' THEN
                    UPDATE public.blog_posts 
                    SET comment_count = comment_count - 1 
                    WHERE id = NEW.blog_post_id;
                ELSIF OLD.moderation_status = 'hidden' AND NEW.moderation_status != 'hidden' THEN
                    UPDATE public.blog_posts 
                    SET comment_count = comment_count + 1 
                    WHERE id = NEW.blog_post_id;
                END IF;
            END IF;
        END IF;
        RETURN NEW;
    ELSIF TG_OP = 'DELETE' THEN
        IF TG_TABLE_NAME = 'blog_post_likes' THEN
            UPDATE public.blog_posts 
            SET like_count = like_count - 1 
            WHERE id = OLD.blog_post_id;
        ELSIF TG_TABLE_NAME = 'blog_post_comments' THEN
            -- Only decrease count if the deleted comment was visible
            IF OLD.moderation_status != 'hidden' THEN
                UPDATE public.blog_posts 
                SET comment_count = comment_count - 1 
                WHERE id = OLD.blog_post_id;
            END IF;
        END IF;
        RETURN OLD;
    END IF;
    RETURN NULL;
END;
$$;


